mapper.select:
	   SELECT b_no, b_title, b_content, b_hitcount, DATE_FORMAT(b_regdate,'%Y-%m-%d') b_regdate, m_id
     FROM board
     WHERE b_no = #{bNo}

SELECT b_no, b_title, b_hitcount, DATE_FORMAT(b_regdate,'%Y-%m-%d') b_regdate, m_id FROM board WHERE b_noticeYN = 1
AND ( b_title like CONCAT('%',#{keyword},'%') OR b_content like CONCAT('%',#{keyword},'%') )
ORDER BY b_no DESC LIMIT #{pagination.startIndex},#{pagination.pageSize}
