mapper.select:
		SELECT COUNT(b.b_no)
		FROM board b



			WHERE b.m_id REGEXP #{word}


			WHERE b.m_id like #{word}


            WHERE b.b_title REGEXP #{word}


            WHERE b.b_content REGEXP #{word}


            WHERE b.b_title REGEXP #{word}
		    OR b.b_content REGEXP #{word}

		SELECT b.b_no, b.b_title, b.b_hitcount, DATE_FORMAT(b.b_regdate,'%Y-%m-%d') b_regdate, b.m_id, r.r_count
		FROM board b
		LEFT JOIN (select r.b_no, count(r.r_no) r_count from reply r) r
		ON b.b_no = r.b_no



			WHERE b.m_id REGEXP #{word}


			WHERE b.m_id like #{word}


            WHERE b.b_title REGEXP #{word}


            WHERE b.b_content REGEXP #{word}


            WHERE b.b_title REGEXP #{word}
		    OR b.b_content REGEXP #{word}



		ORDER BY b.b_no desc
		LIMIT 10 OFFSET #{pagePerList}

SELECT b.b_no, b.b_title, b.b_content, b.b_hitcount, DATE_FORMAT(b.b_regdate,'%Y-%m-%d') b_regdate, b.m_id
FROM board b
WHERE b_no = #{bNo}

mapper.update:
UPDATE board
SET b_title = #{bTitle}, b_content = #{bContent}
WHERE b_no=#{bNo};

UPDATE board
SET b_hitcount = (b_hitcount+1)
WHERE b_no = #{bNo}
