mapper.select:
select m_id, m_name, m_gender, m_birth, m_postcode, m_addr1, m_addr2
from member
where m_id = #{mId} and m_pwd = #{mPwd}

select m_id, m_name, m_gender, m_birth, m_postcode, m_addr1, m_addr2
from member
where m_id = #{mId}

mapper.update:
update member set
m_pwd = #{mPwd}, m_birth = #{mBirth}, m_postcode = #{mPostcode}, m_addr1 = #{mAddr1}, m_addr2 = #{mAddr2}
where m_id = #{mId}
